import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { marked } from "marked";


function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const getAIMessage = async (userQuery) => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/ai-message', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({ input: userQuery })
      });
      if (!response.ok) {
        throw new Error('Network response was not ok.');
      }
      const data = await response.json();
      return { role: 'assistant', content: data }; 
    } catch (error) {
      console.error('Failed to fetch data:', error);
      return { role: 'assistant', content: "Error processing your request." };
    }
  };

  const handleSend = async () => {
    const trimmedInput = input.trim();
    if (trimmedInput) {
      setMessages(prevMessages => [...prevMessages, { role: "user", content: trimmedInput }]);
      setInput("");

      try {
        const response = await getAIMessage(trimmedInput);
        if (response && response.content) {
          // Ensure that the response content is being used here
          setMessages(prevMessages => [...prevMessages, response]);
        } else {
          console.error("No content in response:", response);
        }
      } catch (error) {
        console.error("Error fetching AI message:", error);
      }
    }
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => (
        <div key={index} className={`${message.role}-message-container`}>
          <div className={`message ${message.role}-message`}>
            {message.content && <div dangerouslySetInnerHTML={{ __html: marked(message.content || '') }}></div>}
          </div>
        </div>
      ))}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
        />
        <button className="send-button" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default ChatWindow;
