export const getAIMessage = async (userQuery) => {
  try {
    const response = await fetch('http://127.0.0.1:5000/api/ai-message', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({ input: userQuery })
    });
    if (!response.ok) {
      throw new Error('Network response was not ok.');
    }
    const data = await response.json();
    return { role: 'assistant', content: data.llmResponse };  // Ensure this is the correct key
  } catch (error) {
    console.error('Failed to fetch data:', error);
    return { role: 'assistant', content: "Error processing your request." };
  }
};
