import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
    const trimmedInput = input.trim();
    if (trimmedInput) {
      setMessages(prevMessages => [...prevMessages, { role: "user", content: trimmedInput }]);
      setInput("");

      try {
        const newMessage = await getAIMessage(trimmedInput);
        console.log("Received from backend:", newMessage);  // Log the response received

        // Check if the response has content and display it
        if (newMessage && newMessage.content) {
          setMessages(prevMessages => [...prevMessages, { role: "assistant", content: newMessage.content }]);
        } else {
          // If no content is received, display a generic error message
          setMessages(prevMessages => [...prevMessages, { role: "assistant", content: "Error processing your request. Please try again." }]);
        }
      } catch (error) {
        console.error('Failed to fetch data:', error);  // Log any errors
        setMessages(prevMessages => [...prevMessages, { role: "assistant", content: "Error processing your request." }]);
      }
    }
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => (
        <div key={index} className={`${message.role}-message-container`}>
          <div className={`message ${message.role}-message`}>
            {message.content && <div dangerouslySetInnerHTML={{ __html: marked(message.content || '') }}></div>}
          </div>
        </div>
      ))}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
        />
        <button className="send-button" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default ChatWindow;
